bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    if(record->event.pressed){
	switch (keycode) {
        case UC_CCOP:
	    SEND_STRING("/*");
	    break;
        case UC_CCCL:
	    SEND_STRING("*/");
	    break;
        case UC_CCFL:
            SEND_STRING("/**/");
	    break;
	}
    }
    return true;
}

